import React, { useState, useEffect } from 'react';
import { Card, Container, Row, Col, Form, Button, Spinner, Alert, Carousel } from 'react-bootstrap';
import { getTours } from '../../services/tourService';
import { useAuth } from '../../context/AuthContext';
//import './HomePage.css'; // Create this CSS file for additional styling

const Tours = () => {




  // Testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Sarah Johnson",
      comment: "The best travel experience I've ever had! Everything was perfectly organized.",
      rating: 5,
      tour: "European Adventure"
    },
    {
      id: 2,
      name: "Michael Chen",
      comment: "Excellent service and knowledgeable guides. Will definitely book again!",
      rating: 4,
      tour: "Asian Explorer"
    },
    {
      id: 3,
      name: "Emma Williams",
      comment: "The tour exceeded all my expectations. Worth every penny!",
      rating: 5,
      tour: "African Safari"
    }
  ];

  return (
    <div className="home-page">
      {/* Section 1: Hero Banner */}
      <section className="hero-banner">
        <div className="hero-overlay">
          <Container>
            <div className="hero-content">
              <h1>Discover Your Dream Destination</h1>
              <p className="lead">Explore the world with our expertly crafted tours and travel experiences</p>
              <Button variant="primary" size="lg" href="#tours-section">
                Browse Tours
              </Button>
            </div>
          </Container>
        </div>
      </section>

      {/* Section 2: Featured Tours */}
      <section className="featured-tours py-5 bg-light">
        <Container>
          <h2 className="text-center mb-5">Featured Tours</h2>
          <Row xs={1} md={3} className="g-4">
            {featuredTours.map(tour => (
              <Col key={`featured-${tour.id}`}>
                <Card className="h-100 shadow-sm featured-card">
                  <div className="featured-badge">Popular</div>
                  <Card.Img variant="top" src={tour.imageUrl || 'https://via.placeholder.com/300x200'} />
                  <Card.Body>
                    <Card.Title>{tour.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{tour.destination}</Card.Subtitle>
                    <Card.Text className="text-truncate">{tour.description}</Card.Text>
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="fw-bold text-danger">${tour.price}</span>
                      <span>{tour.duration_days} days</span>
                    </div>
                  </Card.Body>
                  <Card.Footer className="bg-white">
                    <Button 
                      variant="primary" 
                      size="sm"
                      href={`/bookings/new?tourId=${tour.id}`}
                    >
                      Book Now
                    </Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </section>

      {/* Section 3: Testimonials */}
      <section className="testimonials py-5">
        <Container>
          <h2 className="text-center mb-5">What Our Customers Say</h2>
          <Carousel indicators={false} interval={5000}>
            {testimonials.map(testimonial => (
              <Carousel.Item key={testimonial.id}>
                <div className="testimonial-item text-center px-5">
                  <div className="rating mb-3">
                    {[...Array(testimonial.rating)].map((_, i) => (
                      <span key={i} className="star">★</span>
                    ))}
                  </div>
                  <blockquote className="mb-4">
                    "{testimonial.comment}"
                  </blockquote>
                  <footer className="blockquote-footer">
                    {testimonial.name}, <cite>{testimonial.tour}</cite>
                  </footer>
                </div>
              </Carousel.Item>
            ))}
          </Carousel>
        </Container>
      </section>

      {/* Section 4: All Tours (your original code) */}
      <section id="tours-section" className="all-tours py-5 bg-light">
        <Container className="my-5">
          <h2 className="mb-4 text-center" style={{ color: '#3498db' }}>Available Tours</h2>
          
          <Row className="mb-4">
            <Col md={6}>
              <Form.Group>
                <Form.Control
                  type="text"
                  placeholder="Search tours..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={3}>
              <Form.Select
                value={destinationFilter}
                onChange={(e) => setDestinationFilter(e.target.value)}
              >
                <option value="">All Destinations</option>
                {[...new Set(tours.map(tour => tour.destination))].map(dest => (
                  <option key={dest} value={dest}>{dest}</option>
                ))}
              </Form.Select>
            </Col>
            <Col md={3}>
              <Form.Range
                min="0"
                max="10000"
                step="100"
                value={priceRange[1]}
                onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
              />
              <div className="text-center">
                <small>Price up to: ${priceRange[1]}</small>
              </div>
            </Col>
          </Row>
          
          <Row xs={1} md={2} lg={3} className="g-4">
            {filteredTours.map(tour => (
              <Col key={tour.id}>
                <Card className="h-100 shadow-sm">
                  <Card.Img variant="top" src={tour.imageUrl || 'https://via.placeholder.com/300x200'} />
                  <Card.Body>
                    <Card.Title>{tour.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{tour.destination}</Card.Subtitle>
                    <Card.Text className="text-truncate">{tour.description}</Card.Text>
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="fw-bold" style={{ color: '#e74c3c' }}>${tour.price}</span>
                      <span>{tour.duration_days} days</span>
                    </div>
                  </Card.Body>
                  <Card.Footer className="bg-white">
                    <Button 
                      variant="outline-primary" 
                      size="sm" 
                      style={{ borderColor: '#3498db', color: '#3498db' }}
                      href={`/bookings/new?tourId=${tour.id}`}
                    >
                      Book Now
                    </Button>
                    {user && user.roles.includes('ROLE_ADMIN') && (
                      <Button 
                        variant="outline-secondary" 
                        size="sm" 
                        className="ms-2"
                        href={`/admin/tours/edit/${tour.id}`}
                      >
                        Edit
                      </Button>
                    )}
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
          
          {filteredTours.length === 0 && (
            <Alert variant="info" className="mt-4">
              No tours found matching your criteria.
            </Alert>
          )}
        </Container>
      </section>
    </div>
  )

  const [tours, setTours] = useState([]);
  const [filteredTours, setFilteredTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [destinationFilter, setDestinationFilter] = useState('');
  const [priceRange, setPriceRange] = useState([0, 10000]);
  const { user } = useAuth();

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const data = await getTours();
        setTours(data);
        setFilteredTours(data);
      } catch (err) {
        setError('Failed to fetch tours. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  useEffect(() => {
    let results = tours;
    
    if (searchTerm) {
      results = results.filter(tour => 
        tour.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tour.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    if (destinationFilter) {
      results = results.filter(tour => 
        tour.destination.toLowerCase().includes(destinationFilter.toLowerCase())
      );
    }
    
    results = results.filter(tour => 
      tour.price >= priceRange[0] && tour.price <= priceRange[1]
    );
    
    setFilteredTours(results);
  }, [searchTerm, destinationFilter, priceRange, tours]);

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" role="status" style={{ color: '#3498db' }}>
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  // Featured tours (top 3 rated or most popular)
  const featuredTours = tours.slice(0, 3);

}

export default Tours;
