import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { getBookings, createBooking, cancelBooking } from '../../services/bookingService';
import { Table, Button, Container, Spinner, Alert, Modal, Form } from 'react-bootstrap';

const Bookings = () => {
  const { user } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [newBooking, setNewBooking] = useState({
    tourId: '',
    travelersCount: 1,
    bookingDate: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        if (user) {
          const data = await getBookings(user.id);
          setBookings(data);
        }
      } catch (err) {
        setError('Failed to fetch bookings. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, [user]);

  const handleCancel = async (bookingId) => {
    try {
      await cancelBooking(bookingId);
      setBookings(bookings.filter(booking => booking.id !== bookingId));
    } catch (err) {
      setError('Failed to cancel booking. Please try again.');
      console.error(err);
    }
  };

  const handleCreateBooking = async () => {
    try {
      const booking = await createBooking({
        userId: user.id,
        ...newBooking
      });
      setBookings([...bookings, booking]);
      setShowModal(false);
    } catch (err) {
      setError('Failed to create booking. Please try again.');
      console.error(err);
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" role="status" style={{ color: '#3498db' }}>
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container className="my-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 style={{ color: '#3498db' }}>My Bookings</h2>
        <Button variant="success" onClick={() => setShowModal(true)}>New Booking</Button>
      </div>
      
      {bookings.length === 0 ? (
        <Alert variant="info">
          You don't have any bookings yet. Create your first booking to get started!
        </Alert>
      ) : (
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>Tour</th>
              <th>Date</th>
              <th>Travelers</th>
              <th>Price</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map(booking => (
              <tr key={booking.id}>
                <td>{booking.tour.title}</td>
                <td>{new Date(booking.bookingDate).toLocaleDateString()}</td>
                <td>{booking.travelersCount}</td>
                <td>${booking.totalPrice.toFixed(2)}</td>
                <td>
                  <span className={`badge bg-${booking.status === 'CONFIRMED' ? 'success' : 
                                  booking.status === 'CANCELLED' ? 'danger' : 'warning'}`}>
                    {booking.status}
                  </span>
                </td>
                <td>
                  {booking.status === 'PENDING' && (
                    <Button 
                      variant="outline-danger" 
                      size="sm"
                      onClick={() => handleCancel(booking.id)}
                    >
                      Cancel
                    </Button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>New Booking</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Tour ID</Form.Label>
              <Form.Control 
                type="text" 
                value={newBooking.tourId}
                onChange={(e) => setNewBooking({...newBooking, tourId: e.target.value})}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Travelers Count</Form.Label>
              <Form.Control 
                type="number" 
                min="1"
                value={newBooking.travelersCount}
                onChange={(e) => setNewBooking({...newBooking, travelersCount: parseInt(e.target.value)})}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Booking Date</Form.Label>
              <Form.Control 
                type="date" 
                value={newBooking.bookingDate}
                onChange={(e) => setNewBooking({...newBooking, bookingDate: e.target.value})}
                required
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Close</Button>
          <Button variant="primary" onClick={handleCreateBooking}>Create Booking</Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Bookings;
