import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Container, 
  Row, 
  Col, 
  Form, 
  Button, 
  Spinner, 
  Alert, 
  Carousel 
} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Your existing CSS

const Tours = () => {
  // Static mock data - no backend needed
  const mockTours = [
    {
      id: 1,
      title: "European Adventure",
      destination: "Paris, France",
      description: "7-day tour through the heart of Europe including Eiffel Tower and Louvre visits.",
      price: 1200,
      duration_days: 7,
      imageUrl: "https://images.unsplash.com/photo-1431274172761-fca41d930114?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
    },
    {
      id: 2,
      title: "Asian Explorer",
      destination: "Tokyo, Japan",
      description: "10-day cultural journey through Tokyo, Kyoto, and Osaka.",
      price: 1800,
      duration_days: 10,
      imageUrl: "https://images.unsplash.com/photo-1492571350019-22de08371fd3?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
    },
    {
      id: 3,
      title: "Tropical Paradise",
      destination: "Bali, Indonesia",
      description: "Relaxing 8-day beach vacation with spa treatments and island hopping.",
      price: 1500,
      duration_days: 8,
      imageUrl: "https://images.unsplash.com/photo-1518544866330-95a2ab6f3600?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
    }
  ];

  const [tours, setTours] = useState(mockTours);
  const [filteredTours, setFilteredTours] = useState(mockTours);
  const [loading, setLoading] = useState(false); // No loading needed for static data
  const [searchTerm, setSearchTerm] = useState('');
  const [destinationFilter, setDestinationFilter] = useState('');
  const [priceRange, setPriceRange] = useState([0, 10000]);

  // Filter logic
  useEffect(() => {
    let results = tours;
    
    if (searchTerm) {
      results = results.filter(tour => 
        tour.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        tour.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    if (destinationFilter) {
      results = results.filter(tour => 
        tour.destination.toLowerCase().includes(destinationFilter.toLowerCase())
      );
    }
    
    results = results.filter(tour => 
      tour.price >= priceRange[0] && tour.price <= priceRange[1]
    );
    
    setFilteredTours(results);
  }, [searchTerm, destinationFilter, priceRange, tours]);

  // Testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Sarah Johnson",
      comment: "The European tour was absolutely breathtaking! Everything was perfectly organized.",
      rating: 5,
      avatar: "https://randomuser.me/api/portraits/women/32.jpg"
    },
    {
      id: 2,
      name: "Michael Chen",
      comment: "Excellent guides and comfortable accommodations. Will definitely travel with them again!",
      rating: 4,
      avatar: "https://randomuser.me/api/portraits/men/75.jpg"
    }
  ];

  // Featured tours (first 2 from mock data)
  const featuredTours = mockTours.slice(0, 2);

  return (
    <div className="tours-page">
      {/* ===== HERO SECTION ===== */}
      <section className="hero-section">
        <div className="hero-overlay">
          <Container className="text-center text-white py-5">
            <h1 className="display-4 fw-bold mb-4">Discover Your Dream Vacation</h1>
            <p className="lead mb-5">Explore the world with our handcrafted tour experiences</p>
            <Button 
              variant="primary" 
              size="lg" 
              href="#available-tours"
              className="px-4"
            >
              Browse Tours
            </Button>
          </Container>
        </div>
      </section>

      {/* ===== FEATURED TOURS ===== */}
      <section className="py-5 bg-light">
        <Container>
          <h2 className="text-center mb-5">Featured Tours</h2>
          <Row xs={1} md={2} className="g-4">
            {featuredTours.map(tour => (
              <Col key={`featured-${tour.id}`}>
                <Card className="h-100 shadow-sm">
                  <Card.Img 
                    variant="top" 
                    src={tour.imageUrl} 
                    style={{ height: '200px', objectFit: 'cover' }} 
                  />
                  <Card.Body>
                    <Card.Title>{tour.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">
                      {tour.destination}
                    </Card.Subtitle>
                    <Card.Text>{tour.description}</Card.Text>
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="fw-bold text-danger">${tour.price}</span>
                      <span>{tour.duration_days} days</span>
                    </div>
                  </Card.Body>
                  <Card.Footer className="bg-white">
                    <Button variant="primary" size="sm">Book Now</Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </section>

      {/* ===== TESTIMONIALS ===== */}
      <section className="py-5">
        <Container>
          <h2 className="text-center mb-5">Traveler Testimonials</h2>
          <Carousel indicators={false} className="testimonial-carousel">
            {testimonials.map(testimonial => (
              <Carousel.Item key={testimonial.id}>
                <div className="text-center px-5">
                  <img 
                    src={testimonial.avatar} 
                    alt={testimonial.name}
                    className="rounded-circle mb-3"
                    width="80"
                    height="80"
                  />
                  <div className="rating mb-3">
                    {[...Array(testimonial.rating)].map((_, i) => (
                      <span key={i} className="text-warning">★</span>
                    ))}
                  </div>
                  <blockquote className="fs-5 mb-4">
                    "{testimonial.comment}"
                  </blockquote>
                  <footer className="text-muted">
                    — <strong>{testimonial.name}</strong>
                  </footer>
                </div>
              </Carousel.Item>
            ))}
          </Carousel>
        </Container>
      </section>

      {/* ===== ALL TOURS SECTION ===== */}
      <section id="available-tours" className="py-5 bg-light">
        <Container>
          <h2 className="mb-4 text-center text-primary">Available Tours</h2>
          
          {/* Search and Filters */}
          <Row className="mb-4">
            <Col md={6}>
              <Form.Control
                type="text"
                placeholder="Search tours..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </Col>
            <Col md={3}>
              <Form.Select
                value={destinationFilter}
                onChange={(e) => setDestinationFilter(e.target.value)}
              >
                <option value="">All Destinations</option>
                {[...new Set(mockTours.map(tour => tour.destination))].map(dest => (
                  <option key={dest} value={dest}>{dest}</option>
                ))}
              </Form.Select>
            </Col>
            <Col md={3}>
              <Form.Range
                min="0"
                max="10000"
                step="100"
                value={priceRange[1]}
                onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
              />
              <div className="text-center">
                <small>Price up to: ${priceRange[1]}</small>
              </div>
            </Col>
          </Row>
          
          {/* Tours Grid */}
          <Row xs={1} md={2} lg={3} className="g-4">
            {filteredTours.map(tour => (
              <Col key={tour.id}>
                <Card className="h-100 shadow-sm">
                  <Card.Img 
                    variant="top" 
                    src={tour.imageUrl} 
                    style={{ height: '200px', objectFit: 'cover' }} 
                  />
                  <Card.Body>
                    <Card.Title>{tour.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">
                      {tour.destination}
                    </Card.Subtitle>
                    <Card.Text>{tour.description}</Card.Text>
                    <div className="d-flex justify-content-between align-items-center">
                      <span className="fw-bold text-danger">${tour.price}</span>
                      <span>{tour.duration_days} days</span>
                    </div>
                  </Card.Body>
                  <Card.Footer className="bg-white">
                    <Button variant="outline-primary" size="sm">Book Now</Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
          
          {filteredTours.length === 0 && (
            <Alert variant="info" className="mt-4">
              No tours found matching your criteria.
            </Alert>
          )}
        </Container>
      </section>
    </div>
  );
};

export default Tours;
