import api from './api';

export const login = async (credentials) => {
  try {
    console.log('Attempting login with credentials:', { username: credentials.username });
    const response = await api.post('/api/auth/login', credentials);
    console.log('Login response:', response.data);
    
    const { token, id, username, email, roles } = response.data;
    
    // Store the token
    localStorage.setItem('token', token);
    
    // Return the user data
    return {
      token,
      id,
      username,
      email,
      roles
    };
  } catch (error) {
    console.error('Login error details:', error.response || error);
    if (!error.response) {
      throw new Error('Unable to connect to the server. Please check if the backend is running.');
    }
    throw new Error(error.response?.data?.message || 'Invalid username or password.');
  }
};

export const logout = () => {
  try {
    localStorage.removeItem('token');
  } catch (error) {
    console.error('Error during logout:', error);
  }
};

export const register = async (userData) => {
  try {
    console.log('Sending registration request with data:', userData);
    const response = await api.post('/api/auth/register', userData);
    console.log('Registration response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Registration error details:', error.response || error);
    if (!error.response) {
      throw new Error('Unable to connect to the server. Please check if the backend is running.');
    }
    if (error.response?.status === 409) {
      throw new Error('Username or email already exists');
    }
    throw new Error(error.response?.data?.message || 'Failed to register. Please try again.');
  }
};

export const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return !!token;
};
